INSERT INTO `cms_surname` VALUES (7, '云梦江氏', '\\/uploads\\/20191108/qT76XFGSGGXSlNh5zoPeYS7UihMsZbpMvCqsP4jr.jpeg', '云梦江氏', '<p><span style=\"color: #333333; font-family: arial, 宋体, sans-serif; font-size: 14px; text-indent: 28px; background-color: #FFFFFF;\">仙门世家四大家族之一，家族所在为莲花坞，家徽为九瓣莲。先祖江迟为游侠出身，立“明知不可为而为之”为唯一家训。</span></p>', 'admin', 1, '顶级机构', '200001', 0, '2019-11-08 17:58:29', '2019-11-08 17:58:29');
INSERT INTO `cms_surname` VALUES (8, '姑苏蓝氏', '\\/uploads\\/20191108/ka4aQloJmuvnIrPYmUQtqpxIP3TAqmggI6teJ1uZ.png', '姑苏蓝氏', '<p><span style=\"color: #333333; font-family: arial, 宋体, sans-serif; font-size: 14px; text-indent: 28px; background-color: #FFFFFF;\">仙门世家四大家族之一，家族所在为云深不知处，雅正为训，精通音律。家徽为卷云纹，共有四千多条家规。先祖蓝安出身庙宇，后为遇一人而入红尘，还俗为乐师。</span></p>', 'admin', 1, '顶级机构', '200001', 0, '2019-11-08 17:58:20', '2019-11-08 17:58:20');
