  `top` tinyint(1) NOT NULL DEFAULT 0 COMMENT '1置顶，0默认',
  `sort` int(10) NOT NULL DEFAULT 0 COMMENT '排序，值越大排的越靠前，top优先',
  `deleted` tinyint(4) NULL DEFAULT 0 COMMENT '是否删除，0否，1是',
